      Planet.create! valid_attributes
      get planets_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      planet = Planet.create! valid_attributes
      get planet_url(planet)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_planet_url
      expect(response).to be_successful
    end
