      planet = Planet.create! valid_attributes
      expect {
        delete planet_url(planet)
      }.to change(Planet, :count).by(-1)
    end

    it "redirects to the planets list" do
      planet = Planet.create! valid_attributes
      delete planet_url(planet)
      expect(response).to redirect_to(planets_url)
